from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List, Optional
from datetime import date
import csv
from fastapi.responses import StreamingResponse
import io

app = FastAPI()

class Transaction(BaseModel):
    id: int
    type: str  # 'income' or 'expense'
    amount: float
    category: str
    date: date
    description: str

class Budget(BaseModel):
    category: str
    monthly_limit: float

transactions = []
budgets = [Budget(category='Food', monthly_limit=300), Budget(category='Rent', monthly_limit=1000)]

@app.post('/transactions/')
def add_transaction(transaction: Transaction):
    transactions.append(transaction)
    return {'message': 'Transaction added'}

@app.get('/transactions/')
def get_transactions(start_date: Optional[date] = None, end_date: Optional[date] = None):
    filtered = transactions
    if start_date:
        filtered = [t for t in filtered if t.date >= start_date]
    if end_date:
        filtered = [t for t in filtered if t.date <= end_date]
    return filtered

@app.get('/budgets/')
def get_budgets():
    return budgets

@app.get('/analytics/spending_by_category')
def spending_by_category():
    spending = {}
    for t in transactions:
        if t.type == 'expense':
            spending[t.category] = spending.get(t.category, 0) + t.amount
    return spending

@app.get('/export/')
def export_data():
    output = io.StringIO()
    writer = csv.writer(output)
    writer.writerow(['ID', 'Type', 'Amount', 'Category', 'Date', 'Description'])
    for t in transactions:
        writer.writerow([t.id, t.type, t.amount, t.category, t.date, t.description])
    output.seek(0)
    return StreamingResponse(output, media_type='text/csv', headers={'Content-Disposition': 'attachment; filename=transactions.csv'})
