from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List, Optional
from uuid import uuid4

app = FastAPI()

class Course(BaseModel):
    id: str
    title: str
    description: str
    status: str = "active"

class Lesson(BaseModel):
    id: str
    course_id: str
    title: str
    content_type: str
    content: str
    completed: bool = False

class Quiz(BaseModel):
    id: str
    course_id: str
    question: str
    options: List[str]
    answer: int

class Enrollment(BaseModel):
    user_id: str
    course_id: str
    progress: int = 0

courses_db = []
lessons_db = []
quizzes_db = []
enrollments_db = []

@app.post("/courses/")
def create_course(course: Course):
    course.id = str(uuid4())
    courses_db.append(course)
    return course

@app.get("/courses/")
def get_courses():
    return courses_db

@app.post("/lessons/")
def create_lesson(lesson: Lesson):
    lesson.id = str(uuid4())
    lessons_db.append(lesson)
    return lesson

@app.get("/lessons/{course_id}")
def get_lessons(course_id: str):
    return [l for l in lessons_db if l.course_id == course_id]

@app.post("/enroll/")
def enroll(enrollment: Enrollment):
    enrollments_db.append(enrollment)
    return enrollment

@app.get("/progress/{user_id}/{course_id}")
def get_progress(user_id: str, course_id: str):
    for e in enrollments_db:
        if e.user_id == user_id and e.course_id == course_id:
            return {"progress": e.progress}
    raise HTTPException(status_code=404, detail="Enrollment not found")

@app.post("/complete_lesson/")
def complete_lesson(user_id: str, course_id: str, lesson_id: str):
    for e in enrollments_db:
        if e.user_id == user_id and e.course_id == course_id:
            e.progress += 10
            return {"message": "Lesson completed", "progress": e.progress}
    raise HTTPException(status_code=404, detail="Enrollment not found")

@app.post("/certificate/{user_id}/{course_id}")
def generate_certificate(user_id: str, course_id: str):
    for e in enrollments_db:
        if e.user_id == user_id and e.course_id == course_id and e.progress >= 100:
            return {"certificate": f"Certificate of Completion for {course_id} issued to {user_id}"}
    raise HTTPException(status_code=400, detail="Course not completed")
