from fastapi import FastAPI
from datetime import datetime
import pytz
from fastapi.middleware.cors import CORSMiddleware

app = FastAPI()

app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],
    allow_methods=["*"],
    allow_headers=["*"],
)

@app.get("/time/{timezone}")
def get_time(timezone: str):
    try:
        tz = pytz.timezone(timezone)
        now = datetime.now(tz)
        return {
            "hours": now.hour,
            "minutes": now.minute,
            "seconds": now.second,
            "timezone": timezone
        }
    except:
        return {"error": "Invalid timezone"}

@app.get("/timezones")
def get_timezones():
    return {"timezones": pytz.all_timezones[:10]}
