from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List, Optional
from datetime import date
from uuid import uuid4

app = FastAPI()

class Task(BaseModel):
    id: str
    title: str
    description: str
    project_id: str
    assignee: Optional[str] = None
    deadline: Optional[date] = None
    status: str = "todo"

class Project(BaseModel):
    id: str
    name: str
    description: str
    tasks: List[str] = []

db_projects = {}
db_tasks = {}

@app.post("/projects/")
def create_project(name: str, description: str):
    project_id = str(uuid4())
    db_projects[project_id] = Project(id=project_id, name=name, description=description)
    return {"id": project_id}

@app.post("/tasks/")
def create_task(title: str, description: str, project_id: str):
    if project_id not in db_projects:
        raise HTTPException(status_code=404, detail="Project not found")
    task_id = str(uuid4())
    task = Task(id=task_id, title=title, description=description, project_id=project_id)
    db_tasks[task_id] = task
    db_projects[project_id].tasks.append(task_id)
    return {"id": task_id}

@app.put("/tasks/{task_id}/assign")
def assign_task(task_id: str, assignee: str):
    if task_id not in db_tasks:
        raise HTTPException(status_code=404, detail="Task not found")
    db_tasks[task_id].assignee = assignee
    return {"message": "Task assigned"}

@app.put("/tasks/{task_id}/status")
def update_status(task_id: str, status: str):
    if task_id not in db_tasks:
        raise HTTPException(status_code=404, detail="Task not found")
    db_tasks[task_id].status = status
    return {"message": "Status updated"}

@app.get("/projects/{project_id}/progress")
def get_progress(project_id: str):
    if project_id not in db_projects:
        raise HTTPException(status_code=404, detail="Project not found")
    tasks = [db_tasks[tid] for tid in db_projects[project_id].tasks if tid in db_tasks]
    done_count = sum(1 for t in tasks if t.status == "done")
    total = len(tasks)
    progress = (done_count / total * 100) if total > 0 else 0
    return {"progress": progress}
