from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import List, Optional
from datetime import date
from enum import Enum

app = FastAPI()

class Priority(str, Enum):
    high = "high"
    medium = "medium"
    low = "low"

class Task(BaseModel):
    id: int
    title: str
    due_date: date
    priority: Priority
    completed: bool = False

tasks = []
next_id = 1

@app.get("/tasks", response_model=List[Task])
def get_tasks(priority: Optional[Priority] = None, sort_by_date: bool = False):
    filtered = tasks
    if priority:
        filtered = [t for t in filtered if t.priority == priority]
    if sort_by_date:
        filtered = sorted(filtered, key=lambda t: t.due_date)
    return filtered

@app.post("/tasks", response_model=Task)
def create_task(title: str, due_date: date, priority: Priority):
    global next_id
    task = Task(id=next_id, title=title, due_date=due_date, priority=priority)
    tasks.append(task)
    next_id += 1
    return task

@app.put("/tasks/{task_id}/complete")
def complete_task(task_id: int):
    for task in tasks:
        if task.id == task_id:
            task.completed = True
            return {"message": "Task marked complete"}
    raise HTTPException(status_code=404, detail="Task not found")

@app.delete("/tasks/{task_id}")
def delete_task(task_id: int):
    global tasks
    tasks = [t for t in tasks if t.id != task_id]
    return {"message": "Task deleted"}
